# solution for ba1g
def ba1g(str1, str2):
    hamming_distance = 0
    for i in range(len(str1)):
        if str1[i] != str2[i]:
            hamming_distance += 1
    return hamming_distance


# solution for ba1h
def ba1h(pattern, text, d):
    result = []
    for i in range(len(text)-len(pattern)):
        if ba1g(text[i:i+len(pattern)], pattern) <= d:
            result.append(i)
    return result



