# solution for ba1i
def ba1i(text, k, d):
    bases = ['A', 'T', 'C', 'G']
    possible_kmers = generate_kmers(k, bases)
    
    my_dict = {}
    for i in range(len(text) - k + 1):
        a = text[i:i+k]
        for kmer in possible_kmers:
            if ba1g(kmer, a) <= d:
                my_dict[kmer] = 1 + my_dict.get(kmer, 0)

    max_val = max(my_dict.values())
    result = ''
    for key in my_dict:
        if my_dict[key] == max_val:
            result += ' '
            result += key
    print(result)

def generate_kmers(k, bases):
    if k == 1:
        return bases
    small_kmers = generate_kmers(k-1, bases)
    kmers = []
    for kmer in small_kmers:
        for b in bases:
            kmers.append(kmer+b)
    return kmers

