# solution for BA1J
def ba1j(text, k, d):
    bases = ['A', 'T', 'C', 'G']
    possible_kmers = generate_kmers(k, bases)
    
    my_dict = {}
    
    for kmer in possible_kmers:
        f1 = len(ba1h(kmer, text, d))
        f2 = len(ba1h(reverse_complement(kmer), text, d))
            
        my_dict[kmer] = f1 + f2

    max_val = max(my_dict.values())
    
    result = ''
    for key in my_dict:
        if my_dict[key] == max_val:
            result += ' '
            result += key
    print(result)


def reverse_complement(pattern):
    reverse_complement = ''
    for i in range(len(pattern)-1, -1, -1):
        if pattern[i] == 'A':
            reverse_complement += 'T'
        elif pattern[i] == 'C':
            reverse_complement += 'G' 
        elif pattern[i] == 'G':
            reverse_complement += 'C'         
        else:
            reverse_complement += 'A'

    return(reverse_complement)

print(reverse_complement('AACCAGATC'))

